import { useState } from 'react';
import './ExpenseForm.css';

const ExpenseForm = () => {
    // const [inputTitle, setInputTitle] = useState('');
    // const [inputAmount, setInputAmount] = useState('');
    // const [inputDate, setInputDate] = useState('');

    const [userInput, setUserInput] = useState(
        { 
            inputTitle: '', 
            inputAmount: '', 
            inputDate: ''
        })

    const titleChangeHandler = (e) => {
        // setInputTitle(e.target.value)
        // setUserInput( {
        //     ...userInput,
        //     inputTitle: e.target.value,
        // })
        setUserInput((prevState) => {
            return {...prevState, inputTitle: e.target.value};
        })
    };

    const amountChangeHandler = (e) => {
        // setInputAmount(e.target.value)
        setUserInput( {
            ...userInput,
            inputAmount: e.target.value,
        })
        setUserInput((prevState) => {
            return {...prevState, inputA: e.target.value};
        })
    };

    const dateChangeHandler = (e) => {
        // setInputDate(e.target.value)
        setUserInput( {
            ...userInput,
            inputDate: e.target.value,
        })
        setUserInput((prevState) => {
            return {...prevState, inputDate: e.target.value};
        })
    };

    return <form>
        <div className='controls'>
            <div className='control'>
                <label>Title</label>
                <input type='text' onChange={titleChangeHandler}/>
            </div>
            <div className='control'>
                <label>Amount</label>
                <input type='number' min='0.001' step='0.01' onChange={amountChangeHandler}/>
            </div>
            <div className='control'>
                <label>Date</label>
                <input type='date' min='2019-01-01' max='2030-12-31' onChange={dateChangeHandler}/>
            </div>
            <button type='submit' className='action'>Add Expense</button>
        </div>
    </form>
}

export default ExpenseForm;
